import { Component, HostListener, ElementRef} from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  public activeSection: string = "landing";
  private sections: HTMLElement[] = [];

  constructor(private elementRef: ElementRef) { }

  ngOnInit(){

  this.sections = Array.from(document.querySelectorAll('.section'));

  const observer = new IntersectionObserver(entries => {
  entries.forEach(entry => {

        if (entry.isIntersecting) {
          console.log("add", this.elementRef)
        } 
        else {

          console.log("Removing", this.elementRef.nativeElement.id)
          this.elementRef.nativeElement.classList.remove('section-in-viewport');
        }
      });
    }, {threshold: 0.4});

    this.sections.forEach(section => observer.observe(section));

  }

  scrollToSection(link: HTMLAnchorElement | null, sectionId: string) {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth',
      block: 'start', // Scroll to the top of the section
      inline: 'nearest' // Scroll to the nearest side if section is partially visible
      });
    }
  }

  private scrollIntoViewSmoothly(element: HTMLElement) {
    element.scrollIntoView({ behavior: 'smooth' });
  }
