import { Component, OnInit, ViewChild, HostListener, ElementRef} from '@angular/core';
import { ProfileInfoService } from '../services/profile-info.service';
@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.css', './landing.figure.css'],

})
export class LandingComponent implements OnInit {
  @ViewChild('scrollContainer', { static: true }) scrollContainer!: ElementRef;

  constructor (private profileInfoService: ProfileInfoService) { }
  parallaxOffset: number = 0;
  logoSource: string = 'assets/LOGO.png'
  profileData: any;
  private wheelListener: ((event: Event) => void) | null = null;

  ngAfterViewInit() {
    // Bind the scroll event listener to the scroll container
    this.wheelListener = this.handleScroll.bind(this);
    this.scrollContainer.nativeElement.addEventListener('scroll', this.wheelListener, { passive: true });
    console.log("INITED")
  }

  private handleScroll() {
    const scrollPosition = this.scrollContainer.nativeElement.scrollTop; // Get the scroll position of the container
    const maxParallaxOffset = this.scrollContainer.nativeElement.clientHeight * 0.3; // 30% of the container's height
    this.parallaxOffset = Math.min(scrollPosition * 0.5, maxParallaxOffset);
    console.log("HANDLING SCROLL")

    // Apply the parallax effect (for example, updating styles dynamically)
    this.applyParallaxEffect();
  }

  private applyParallaxEffect() {
    // Here you can apply the parallax effect, e.g., by modifying styles
    // For instance, setting the transform on an element inside the container
    const parallaxElement = this.scrollContainer.nativeElement.querySelector('.parallax-element');
    if (parallaxElement) {
      parallaxElement.style.transform = `translateY(${this.parallaxOffset}px)`;
    }
  }




  ngOnInit(): void {
    this.getProfileInfoData();
  }


  getProfileInfoData()
  {
    this.profileInfoService.getProfileData().subscribe(
      {
        next: (data) =>{
          this.profileData = data;
        },
        error: (error) => {
          console.log(error)
        }
      }
    )
  }
}
<section id="landing" class="section">
  <div class="parallax-container">
    <!---Backgroun with parallax-->
    <div
      class="landing-background"
      [style.transform]="'translateY(' + parallaxOffset + 'px)'"
    >
      <div class="landing-padding-wrapper">
        <div class="landing-inner-wrapper">
          <div class="quote-container">
            <div class="quote">
              <span class="quote-text">{{ profileData?.quote }}</span>
              <span class="quote-author">-Latvian saying</span>
            </div>
          </div>
          <div class="logo-wrapper">
            <div class="logo-container">
              <img [src]="logoSource" class="logo" />
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  <!---Backgroun with parallax end-->
  <div class="content">
    <div class="figure-wrapper">
      <div class="landing-figure">
        <p class="description">{{ profileData?.description }}</p>
      </div>
      <div class="landing-figure-footer"></div>
    </div>
  </div>
</section>
