import { Component, Renderer2, HostListener} from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  public activeSection: string = "landing";
  public sectionElement: any = document.getElementById("landing")!;
  public enableScrolling: boolean = true;
  public scrollingTimer: any;
  public autoScroll: any;

  constructor(private renderer: Renderer2) { }



  scrollToSection(link: HTMLAnchorElement | null, sectionId: string) {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth',
      block: 'start', // Scroll to the top of the section
      inline: 'nearest' // Scroll to the nearest side if section is partially visible
      });
    }
  }


  @HostListener('window:scroll', ['$event'])
  onScroll(event: Event){

    // Determine the active section based on scroll position
    const sections = document.querySelectorAll('.section');
    let activeSection = null;
    sections.forEach((section)=>{
      const sectionTop = section.getBoundingClientRect().top;
      if(sectionTop<=500)
      {
        this.sectionElement = section;
        this.activeSection = section.id;
      }
    });
  }








}

<nav class="navbar">
  <div class="navbar-inner">
    <a
      #homeLink
      (click)="scrollToSection(homeLink, 'landing')"
      [ngClass]="{ active: activeSection === 'landing' }"
      class="navbar-link"
      class="navbar-link active"
      >Home</a
    >
    <a
      #menuLink
      (click)="scrollToSection(menuLink, 'main-menu')"
      [ngClass]="{ active: activeSection === 'main-menu' }"
      class="navbar-link"
      >About</a
    >
    <a
      #contactLink
      (click)="scrollToSection(menuLink, 'contact-info')"
      [ngClass]="{ active: activeSection === 'contact-info' }"
      class="navbar-link"
      >Contact</a
    >
  </div>
</nav>
