import { Directive, ElementRef, OnInit  } from '@angular/core';
@Directive({
  selector: '[appSectionInViewport]'
})
export class SectionInViewportDirective {

  constructor(private elementRef: ElementRef) { }
  ngOnInit(){
  const observer = new IntersectionObserver(entries => {
    entries.forEach(entry => {
        if (entry.isIntersecting) {

          if (!this.elementRef.nativeElement.classList.contains('section-in-viewport')) {
            // Scroll to the element when it enters the viewport

              this.scrollToElement();


            this.elementRef.nativeElement.scrollIntoView({ behavior: 'smooth' });
          }

          this.elementRef.nativeElement.classList.add('section-in-viewport');

          console.log("add", this.elementRef.nativeElement.id)
        } else {
          // Element is not in the viewport
          console.log("Removing", this.elementRef.nativeElement.id)
          this.elementRef.nativeElement.classList.remove('section-in-viewport');
        }
      });
  }, {threshold: 0.4});
        observer.observe(this.elementRef.nativeElement);

  }

    private scrollToElement() {
    // Calculate the desired scroll position relative to the viewport
    const offsetTop = this.elementRef.nativeElement.getBoundingClientRect().top;

    // Scroll the window to the element's position
    window.scrollTo({
      top: window.scrollY + offsetTop,
      behavior: 'smooth'
    });
  }


}
