import { Directive, ElementRef, OnInit  } from '@angular/core';
@Directive({
  selector: '[appSectionInViewport]'
})
export class SectionInViewportDirective {

  constructor(private elementRef: ElementRef) { }
  ngOnInit(){
  const observer = new IntersectionObserver(entries => {
    entries.forEach(entry => {
        if (entry.isIntersecting) {
          // Element is in the viewport

          this.elementRef.nativeElement.classList.add('section-in-viewport');
        } else {
          // Element is not in the viewport
          this.elementRef.nativeElement.classList.remove('section-in-viewport');
        }
      });
  });
        observer.observe(this.elementRef.nativeElement);

  }

}
