import { Component, Renderer2, HostListener } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  public activeSection: string = "landing";
  constructor(private renderer: Renderer2) { }

  scrollToSection(link: HTMLAnchorElement, sectionId: string) {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  }

  @HostListener('window:scroll', ['$event'])
  onScroll(event: Event){
    const landingSection = document.getElementById("landing")
    const mainMenuSection = document.getElementById("main-menu")
    const contactInfoSection = document.getElementById("contact-info")
    const offset = 500;
    if(landingSection && window.scrollY >= landingSection.offsetTop - offset &&
      window.scrollY < mainMenuSection!.offsetTop - offset) {
        this.activeSection = "landing";
      }
    else if(mainMenuSection && window.scrollY >= mainMenuSection.offsetTop - offset &&
      window.scrollY < contactInfoSection!.offsetTop - offset) {
        this.activeSection = "main-menu";
      }
    else if (contactInfoSection && window.scrollY >= contactInfoSection.offsetTop - offset) {
      this.activeSection = "contact-info";
      }

}
}
