import { Component, ElementRef, HostListener, ViewChild } from '@angular/core';
import { LandingScrollService } from './services/landing-scroll.service';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild('scrollContainer', { static: true }) scrollContainer!: ElementRef;
  private isScrolling = false;
  private readonly scrollDelay = 100;
  title = 'frontent_portfolio';

  ngAfterViewInit() {
    this.scrollContainer.nativeElement.addEventListener('wheel', this.handleWheel.bind(this), { passive: false });
  }


  constructor(private scrollService: LandingScrollService) {}

  @HostListener('scroll', ['$event'])
  onContainerScroll(event: Event) {
    /*
    const container = this.scrollContainer.nativeElement;
    const scrollY = container.scrollTop;
    this.scrollService.setScrollValue(scrollY);
    */


  }


}
<app-navbar></app-navbar>
<section
  #scrollContainer
  id="scrollContainer"
  (scroll)="onContainerScroll($event)"
>
  <div>
    <app-landing></app-landing>
  </div>
  <div>
    <app-main-menu></app-main-menu>
  </div>
  <div>
    <app-contact-info></app-contact-info>
  </div>
</section>
<router-outlet></router-outlet>
