import { Component, OnInit, HostListener } from '@angular/core';
import { ProfileInfoService } from '../services/profile-info.service';
import { trigger, style, animate, transition } from '@angular/animations';
@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.css'],
  animations: [
    trigger('parallax', [
      transition(':enter', [
      style({ transform: 'translateY(-50%)'}),
      animate('500ms', style({transform: 'translateY(0)'})),
    ]),
  ]),
  ],
})
export class LandingComponent implements OnInit {
  constructor (private profileInfoService: ProfileInfoService) { }
  parallaxOffset = 0;

  ngOnInit(): void {
    this.getProfileInfoData();
  }
  @HostListener('window:scroll', [])
  onWindowScroll() {
    this.parallaxOffset = window.scrollY * 0.5; // Adjust the speed as needed
  }

  getProfileInfoData()
  {
    console.log(1)
    this.profileInfoService.getProfileData().subscribe(
      {
        next: (data) =>{
          console.log(data)
        },
        error: (error) => {
          console.log(error)
        }
      }
    )
  }
}
