import { Component, ElementRef, HostListener} from '@angular/core';
import { TimeoutConfig } from 'rxjs';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  public activeSection: string = "landing";
  private sections: HTMLElement[] = [];
  private scrollEnded: boolean = false;
  private scrollTimeout: any;
  private sectionScroll: any;



  constructor(private elementRef: ElementRef) { }

  ngOnInit(){
  this.sections = Array.from(document.querySelectorAll('.section'));

  const observer = new IntersectionObserver(entries => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        //change active link
        this.activeSection = entry.target.id
      }       
    });
  }, 
  {threshold: 0.4});

  this.sections.forEach(section => observer.observe(section));
  }

  scrollToSection(link: HTMLAnchorElement | null, sectionId: string) {

    //get section when link is clicked
    const element = document.getElementById(sectionId);
    //if section exists scroll to section
    if (element) {
      element.scrollIntoView({ behavior: 'smooth',
      block: 'center', 
      inline: 'nearest'
      });
    }
  }

  @HostListener('window:scroll', ['$event'])
  onScroll()
  {
    console.log("SCROLL EVENT")
    // Use a timeout to detect when the user has stopped scrolling
    clearTimeout(this.scrollTimeout);
    this.scrollTimeout = setTimeout(() => {
      this.scrollEnded = true;
      this.scrollToSection(null, this.activeSection)

      }, 50); // Adjust the timeout duration as needed
  }




}

<nav class="navbar">
  <div class="navbar-inner">
    <a
      #homeLink
      (click)="scrollToSection(homeLink, 'landing')"
      [ngClass]="{ active: activeSection === 'landing' }"
      class="navbar-link"
      class="navbar-link active"
      >Home</a
    >
    <a
      #menuLink
      (click)="scrollToSection(menuLink, 'main-menu')"
      [ngClass]="{ active: activeSection === 'main-menu' }"
      class="navbar-link"
      >About</a
    >
    <a
      #contactLink
      (click)="scrollToSection(menuLink, 'contact-info')"
      [ngClass]="{ active: activeSection === 'contact-info' }"
      class="navbar-link"
      >Contact</a
    >
  </div>
</nav>
