import { Component, OnInit, HostListener, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { ProfileInfoService } from '../services/profile-info.service';
@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.css'],

})
export class LandingComponent implements OnInit {
  @ViewChild('parallaxElement', { static: false }) parallaxElement!: ElementRef;

  constructor (private profileInfoService: ProfileInfoService, private el: ElementRef, private renderer: Renderer2) { }
  parallaxOffset: number = 0;
  logoSource: string = 'assets/LOGO.png'
  profileData: any;

  ngOnInit(): void {
    //this.getProfileInfoData();
  }
  ngAfterViewInit(): void {
    // Check if the renderer is connected to the parallax element
    if(this.parallaxElement)
    {
      this.setupParallax()
    }
  }

  @HostListener('window:scroll', [])
  onWindowScroll() {
    this.parallaxOffset = window.scrollY * 0.8; // Adjust the speed as needed

  }
  setupParallax() {
    // Ensure that the renderer is connected to the element
    console.log(this.parallaxElement)
    this.renderer.setStyle(
      this.parallaxElement.nativeElement,
      'transform',
      `translate3d(0, ${this.parallaxOffset}px, 0)`
    );
  }

  getProfileInfoData()
  {
    console.log(1)
    this.profileInfoService.getProfileData().subscribe(
      {
        next: (data) =>{
          console.log(data)
          this.profileData = data;
        },
        error: (error) => {
          console.log(error)
        }
      }
    )
  }
}
<section class="section-window landing-window">
  <div #parallaxElement class="landing-background">
    <div class="quote-container">
      <div class="quote">
        <span class="quote-text">{{ profileData?.quote }}</span>
        <span class="quote-author">-Latvian saying</span>
      </div>
    </div>
    <div class="logo-container"><img [src]="logoSource" class="logo" /></div>
  </div>
  <div class="figure-wrapper">
    <div class="landing-figure">
      <p class="description">{{ profileData?.description }}</p>
    </div>
    <div class="landing-figure-footer"></div>
  </div>
</section>
