import { Component, Input, OnInit } from '@angular/core';
import { ProfileInfoService } from '../services/profile-info.service';
@Component({
  selector: 'app-contact-info',
  templateUrl: './contact-info.component.html',
  styleUrls: ['./contact-info.component.css']
})
export class ContactInfoComponent {
  constructor (private profileInfoService: ProfileInfoService) { }

  profileData: any;
  ngOnInit(): void {
    this.getProfileInfoData();
  }
  getProfileInfoData()
  {
    this.profileInfoService.getProfileData().subscribe(
      {
        next: (data) =>{
          console.log(data)
          this.profileData = data;
        },
        error: (error) => {
          console.log(error)
        }
      }
    )
  }
}
<section id="contact-info">
  <div class="contact-info-container">
    <div class="contact-info-header">
      <h2 class="contact-title">Contact me</h2>
      <span class="contact-email"> {{ profileData?.email }}</span>
    </div>
    <div class="email-form">
      <h3 class="contact-input-title">Send me an email:</h3>
      <input type="text" rows="1" placeholder="email" class="email-input" />
      <textarea rows="8" placeholder="Message" class="email-input"></textarea>
      <div>
        <a class="email-submit-btn">Send</a>
      </div>
    </div>
  </div>
</section>
